package importantStringConcepts;

public class TrianglePattern {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		 for(int i=5; i>=0; i--)
		 {
		 System.out.println();
		 for(int j=i; j<5;j++)
		 {
		 System.out.print(" * ");
		 }
		 }
		 System.out.println();
	}

}
